package com.chimngu.drag.utils.images;import android.content.Context;import android.graphics.Bitmap;import android.widget.ImageView;import androidx.annotation.DrawableRes;import com.bumptech.glide.Glide;import com.bumptech.glide.load.Transformation;import com.bumptech.glide.load.engine.DiskCacheStrategy;import static com.bumptech.glide.load.resource.bitmap.BitmapTransitionOptions.withCrossFade;public class ImageUtils {    public static void showImage(Context context, ImageView imageView, @DrawableRes int thumb, Transformation<Bitmap>... transformations) {        Glide.with(context)                .asBitmap()                .transition(withCrossFade(100))                .transform(transformations)                .diskCacheStrategy(DiskCacheStrategy.NONE)                .skipMemoryCache(true)                .load(thumb)                .into(imageView);    }}